mapper.select:
SELECT 	aptCode, apartmentName, buildYear, roadName, bunji, sidoCode, gugunCode, dongCode
FROM	apt_info
WHERE	dongCode = #{dongCode}

SELECT 	aptCode, apartmentName, buildYear, roadName, bunji, sidoCode, gugunCode, dongCode
FROM	apt_info
WHERE	aptCode = #{aptCode}

mapper.update:
UPDATE apt_info
apartmentName = #{apartmentName}, buildYear = #{buildYear}, roadName = #{roadName}, bunji = #{bunji}, sidoCode = #{sidoCode}, gugunCode = #{gugunCode}, dongCode = #{dongCode},
WHERE aptCode = #{aptCode}
