mapper.select:
SELECT 	no, dealAmount, dealYear, dealMonth, dealDay, area, floor, cancelDealDay, aptCode
FROM	apt_deal
WHERE	aptCode = #{aptCode}

SELECT 	no, dealAmount, dealYear, dealMonth, dealDay, area, floor, cancelDealDay, aptCode
FROM	apt_deal
WHERE	no = #{no}

mapper.update:
UPDATE apt_deal
dealAmount = #{dealAmount}, dealYear = #{dealYear}, dealMonth = #{dealMonth}, dealDay = #{dealDay}, area = #{area}, floor = #{floor}, cancelDealDay = #{cancelDealDay}, aptCode = #{aptCode},
WHERE no = #{no}
