mapper.update:
UPDATE user
SET TOKEN = #{token}
WHERE USER_ID = #{userid}

UPDATE user
SET token = #{token, jdbcType=VARCHAR}
where USER_ID = #{userid}

mapper.select:
SELECT token
FROM user
WHERE USER_ID = #{userid}
